import streamlit as st
st.title("chai test poll")

col1,col2,col3,col3=st.columns(4)
with col1:
    st.header("Masala chai")
    st.image("data:image/jpeg;base64,")
    vote1=st.button("vote masala chai")
with col2:
    st.header("Ginger chai")
    st.image("data:image/jpeg;base64,")
    vote2=st.button("vote ginger chai")

if vote1:
    st.success("you voted for masala chai")
elif vote2:
    st.success("you voted for ginger chai")
else:
    st.info("please cast your vote")


name=st.sidebar.text_input("enter your name")
tea=st.sidebar.selectbox("choose you chai ",["masala chai","ginger chai"])
st.write(f"thank you {name} for voting for {tea}!")


with st.expander("show chai makeing instruction:"):
    st.write("""
    1. Boil water
    2. Add tea leaves and spices
    3. Add milk and sugar
    4. Simmer for a few minutes
    5. Strain and serve hot
    """)

st.markdown("### welcome to chai app")
st.markdown(">blockquote: chai is the best")

